import React from 'react'
import s from '../SortingBlock.module.css'
import { useDispatch } from 'react-redux';
import { sortProductsAction } from '../../../store/Sorting_reducer/Sorting_reducer';

export default function SortForm({state}) {

  const sortBy = e => {
      if (e.target.value === 'default') {
        state.sort((a, b) => a.id-b.id)
    } else if(e.target.value === 'title') {
        state.sort((a, b) => a.title.localeCompare(b.title))
    } else if (e.target.value === 'price_low') {
        state.sort((a, b) => a.price-b.price)
    } else if (e.target.value === 'price_high') {
        state.sort((a, b) => b.price-a.price)
    }     
    return state
    console.log(e.target.value)
  }



  // const order = e => dispatch(sortProductsAction(e.target.value))

  return (
    <div className={s.sorting_section}>
        <p className={s.sorting_title}>Sorted</p>
        <select onInput={sortBy} className={s.selector} defaultValue='default'>
            <option  value='default'>By default</option>
            <option value='title'>By alphabet</option>
            <option value='price_low'>By price (lowest first)</option>
            <option value='price_high'>By price (highest firts)</option>
        </select>
    </div>
  )
}
