import React, { useState, useEffect, useRef } from 'react';
import Button from '../Button/Button';
import { useLocation } from 'react-router-dom';

export default function ScrollToTheTop() {
  const [showButton, setShowButton] = useState(false);
  const footerRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      if (window.pageYOffset > 100) {
        setShowButton(true);
      } else {
        setShowButton(false);
      }
    };
    window.addEventListener('scroll', handleScroll);
    footerRef.current = document.querySelector('footer');

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };

  const location = useLocation();
  const wishlist = location.pathname === '/wishlist'
  const cart = location.pathname === '/cart'
  const product = location.pathname === '/product'
  debugger

  useEffect(() => {
    const handleScroll = () => {
      if (footerRef.current) {
        const rect = footerRef.current.getBoundingClientRect();
        if (rect.top >= 0 && rect.bottom <= window.innerHeight) {
          setShowButton(false);
        } else {
          setShowButton(true);
        }
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [footerRef]);

  return (
    <div className='w-1/2 lg:w-1/3 p-5 mx-auto'>
      {showButton && (
        <Button name='Scroll to the top' className='primary' onClick={scrollToTop} />
      )}
    </div>
  );
}
