import { Routes, Route } from 'react-router-dom';
import './App.css';
import Navbar from './components/Navbar/Navbar';
import Home from './pages/Home';
import Categories from './pages/Categories';
import Product from './pages/Product';
import NotFound from './pages/NotFound';
import Products from './pages/Products';
import Sales from './pages/Sales';
import ProductByCategory from './pages/ProductByCategory';
import Footer from './components/Footer/Footer';
import CartPage from './pages/CartPage';
import { useEffect, useState } from 'react';

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
      // Проверяем, есть ли сохраненное состояние в локальном хранилище
      const savedTheme = localStorage.getItem('theme');
      if (savedTheme === 'dark') {
          setIsDarkMode(true);
      } else {
          setIsDarkMode(false);
      }
  }, []);

  useEffect(() => {
      // Обновляем класс темы при изменении состояния
      if (isDarkMode) {
          document.body.classList.add('dark');
          localStorage.setItem('theme', 'dark'); // Сохраняем выбранную тему
      } else {
          document.body.classList.remove('dark');
          localStorage.setItem('theme', 'light'); // Сохраняем выбранную тему
      }
  }, [isDarkMode]);

  const toggleTheme = () => {
      setIsDarkMode(!isDarkMode); // Изменяем состояние для переключения темы
  };

  return (
    <div>
      <Navbar />
      <Routes>
        <Route path={'/'} element={<Home />} />
        <Route path={'/categories'} element={<Categories />} />
        <Route path={'/categories/:id'} element={<ProductByCategory />} />
        <Route path={'/products'} element={<Products />} />
        <Route path={'/cart'} element={<CartPage />} />
        <Route path={'/products/:id'} element={<Product />} />
        <Route path={'/sales'} element={<Sales />} />
        <Route path={'*'} element={<NotFound/>} />
      </Routes>
      <Footer />
    </div>
  );
}

export default App;
