const LOAD_PRODUCTS = 'LOAD_PRODUCTS'
const LOAD_ALL_PRODUCTS = 'LOAD_ALL_PRODUCTS'
const LOAD_SINGLE_PRODUCTS = 'LOAD_SINGLE_PRODUCTS'

export const loadProductsByCategoryAction = payload => ({type: LOAD_PRODUCTS, payload}) 
export const loadAllProductsAction = payload => ({type: LOAD_ALL_PRODUCTS, payload}) 
export const loadSingleProductAction = payload => ({type: LOAD_SINGLE_PRODUCTS, payload})

export const Products_reducer = (state = [], action) => {
    if (action.type === LOAD_PRODUCTS) {
        return action.payload
    } else if (action.type === LOAD_ALL_PRODUCTS) {
        return action.payload
    } else if (action.type === LOAD_SINGLE_PRODUCTS) {
        return action.payload
    } else return state
}
