import React, { useState} from 'react'
import s from '../SortingBlock.module.css'
import { useDispatch } from 'react-redux';
import { getCheapProductAction } from '../../../store/slices/products_slice';


export default function CheapProduct() {
  const [ itemChecked, setItemChecked ] = useState(false)

  const handleChange = () => setItemChecked(!itemChecked)

  const dispatch = useDispatch()

  const getCheapProducts = e => {
    dispatch(getCheapProductAction(e.target.checked))
  }

  return (
    // <label className={s.checkbox_label}>
    //   <input
    //     onChange={handleChange}
    //     checked={itemChecked}
    //     type="checkbox"
    //     className={s.checkbox_input}
    //     onClick={getCheapProducts}
    //   />
    //   <span className={s.checkbox_description}>Discounted products</span>
    // </label>
    <label className={s.switch}>
      <input 
        type='checkbox' 
        data-test-id="switcher"
        className={s.switch_input} 
        checked={itemChecked}
        onChange={handleChange}
      />
      <span className={s.switch_slider}>Discounted products</span>
    </label>
  )
}
