import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { root_url } from '../../global'

const initialState = {
    list: [],
}

export const fetchUsers = createAsyncThunk(
    'Users/fetchUsers',
    async () => {
        const response = await fetch(`${root_url}products/all`);
        const data = await response.json();
        return data.users;
    }
)

const user_slice = createSlice({
    name: 'products',
    initialState,
    reducers: {

    },
    extraReducers: (builder) => {
        builder
            .addCase(fetchUsers.pending, (state) => {    //pending
                state.status = 'loading'
            })
            .addCase(fetchUsers.fulfilled, (state, action) => {   //success
                state.status = 'ready'
                state.list = action.payload
            })
            .addCase(fetchUsers.rejected, (state) => {   //rejected
                state.status = 'error'
            })
    },
})

export default user_slice;
