import React, { useState } from 'react'
import s from './CartPreviewWindow.module.css'
import CloseButton from '../CloseButton/CloseButton'

export default function CartPreviewWindow({ id, title, image, discont_price, description, price }) {
  const [showWindow, setShowWindow] = useState(true)

  const openCartPreview = () => {
    setShowWindow(true);
  };

  const closeCartPreview = () => {
    setShowWindow(false);
  };

  return (
    <div className="w-full h-full">
      <button onClick={openCartPreview}>Add to Cart</button>
      {showWindow && (
        <div className={s.window_wrapper}>
          <span onClick={closeCartPreview}>
            <CloseButton />
          </span>
          <div>
            {/* Отображение информации о добавленном продукте */}
            <h2>{title}</h2>
            <img src={image} alt={title} />
            <p>{description}</p>
            <p>Price: {price}</p>
            <p>Discounted Price: {discont_price}</p>

            {/* Здесь вы можете добавить логику для отображения содержимого корзины и сумму цен */}
          </div>
        </div>
      )}
    </div>
  )
}
