import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { root_url } from '../../global'

const initialState = {
    list: [],
}

export const fetchProducts = createAsyncThunk(
    'Products/fetchProducts',
    async () => {
        const response = await fetch(`${root_url}products/all`);
        const data = await response.json();
        return data;
    }
)

const products_slice = createSlice({
    name: 'products',
    initialState,
    reducers: {
        sortProductsAction(state, action) {
            if (action.payload === 'default') {
                state = state.sort((a, b) => a.id-b.id)
            } else if(action.payload === 'title') {
                state = state.sort((a, b) => a.title.localeCompare(b.title))
            } else if (action.payload === 'price_low') {
                state = state.sort((a, b) => a.price-b.price)
            } else if (action.payload === 'price_high') {
                state = state.sort((a, b) => b.price-a.price)
            } 
        },
        filterProductsAction(state, action) {
            const { min_value, max_value } = action.payload;
            return state.map( el => {
                if (el.price >= min_value && el.price <= max_value){
                    el.show_product = true;
                } else {
                    el.show_product = false;
                }
                return el
            }) 
        },
        getCheapProductAction(state, action) {
            if (action.payload) {
                return state.map(el => {
                    if(!el.discont_price){
                        el.show_product = false
                    }
                    return el
                })
            } else {
                return state.map( el => {
                    el.show_product = true
                    return el
                })
            }
        },
    },
    extraReducers: (builder) => {
        builder
            .addCase(fetchProducts.pending, (state) => {    //pending
                state.status = 'loading'
            })
            .addCase(fetchProducts.fulfilled, (state, action) => {   //success
                state.status = 'ready'
                state.list = action.payload
            })
            .addCase(fetchProducts.rejected, (state) => {   //rejected
                state.status = 'error'
            })
    }
})

export default products_slice.reducer;
export const { loadProducts } = products_slice.actions;
