import React, { useContext } from 'react'
import s from '../SortingBlock.module.css'
import { useDispatch } from 'react-redux';
import { sortProductsAction } from '../../../store/all_products_reducer/all_products_reducer';
import { sortProductsCategoryAction } from '../../../store/products_by_category_reducer/products_by_category_reducer';
import { Context } from '../../../context';

export default function SortForm() {
  const dispatch = useDispatch()
  const { currentPath } = useContext(Context)

  // const order = e => dispatch(sortProductsAction(e.target.value))
  // const order = e => dispatch(sortProductsCategoryAction(e.target.value))

  const order = e => {
    currentPath === '/products' ? dispatch(sortProductsAction(e.target.value)) : 
    dispatch(sortProductsCategoryAction(e.target.value))
  }

  return (
    <div className={s.sorting_section}>
        <p className={s.sorting_title}>Sorted</p>
        <select onInput={order} className={s.selector} defaultValue='default'>
            <option  value='default'>By default</option>
            <option value='title'>By alphabet</option>
            <option value='price_low'>By price (lowest first)</option>
            <option value='price_high'>By price (highest firts)</option>
        </select>
    </div>
  )
}
