import React from 'react'
import s from './ProductDescription.module.css'
import { useDispatch } from 'react-redux';
import Button from '../UI/Button/Button';
import { root_url } from '../../global'
import { addProductToTotalCart } from '../../store/slices/cart_slice';
import Container from '../UI/Container/Container';
import PriceRow from '../UI/PriceRow/PriceRow';
import { addProductToWishlist } from '../../store/slices/wishlist_slice';


export default function ProductDescription({title, price, discont_price, image, description }) {
  const dispatch = useDispatch()

  return (
    <Container>
      <h2 className='h2'>{title}</h2>
        <div className={s.container_card}>
              <div style={{backgroundImage: `url(${root_url}${image})`, backgroundSize: 'cover'}} className={s.img} />
              <div className={s.card_descr}>
                <h2 className='h2'>{title}</h2>
                  <PriceRow price={price} discont_price={discont_price} />
                  <div className={s.buttons_descr}>
                    <Button onClick={ () => dispatch(addProductToTotalCart(title, price, discont_price, image, description))} name='Add to cart' className='primary' />
                    <Button onClick={() => dispatch(addProductToWishlist({title, price, discont_price, image, description}))} name='Add to wishlist' className='primary' />                    
                  </div>
                  <div className='mt-10'>
                      <h4 className='h4'><b>Description:</b></h4>
                      <p className='text-xl'>{description}</p>                             
                  </div>
              </div>
        </div>
    </Container>
  )
}
