import React from 'react'
import s from '../SortingBlock.module.css'
import { useDispatch } from 'react-redux';
import { sortProductsAction } from '../../../store/slices/products_slice';
import Select, { StylesConfig } from 'react-select';

const colourStyles = {
  control: (styles) => ({ ...styles, backgroundColor: 'white' }),
  option: (styles, { data, isDisabled, isFocused, isSelected }) => {
    const color = chroma(data.color);
    return {
      ...styles,
      backgroundColor: isDisabled
        ? undefined
        : isSelected
        ? data.color
        : isFocused
        ? color.alpha(0.1).css()
        : undefined,
      color: isDisabled
        ? '#ccc'
        : isSelected
        ? chroma.contrast(color, 'white') > 2
          ? 'white'
          : 'black'
        : data.color,
      cursor: isDisabled ? 'not-allowed' : 'default',

      ':active': {
        ...styles[':active'],
        backgroundColor: !isDisabled
          ? isSelected
            ? data.color
            : color.alpha(0.3).css()
          : undefined,
      },
    };
  },
  multiValue: (styles, { data }) => {
    const color = chroma(data.color);
    return {
      ...styles,
      backgroundColor: color.alpha(0.1).css(),
    };
  },
  multiValueLabel: (styles, { data }) => ({
    ...styles,
    color: data.color,
  }),
  multiValueRemove: (styles, { data }) => ({
    ...styles,
    color: data.color,
    ':hover': {
      backgroundColor: data.color,
      color: 'white',
    },
  }),
};

export default function SortForm() {
  const dispatch = useDispatch();

  const options = [
    { value: 'default', label: 'By default' },
    { value: 'title', label: 'By alphabet' },
    { value: 'price_low', label: 'By price (lowest first)' },
    { value: 'price_high', label: 'By price (highest first)' },
  ];

  const handleSelectChange = (selectedOption) => {
    if (selectedOption) {
      dispatch(sortProductsAction(selectedOption.value));
    }
  };

  return (
    <div className={s.sorting_section}>
      <p className={s.sorting_title}>Sorted</p>
      <Select
        options={options}
        defaultValue={options[0]}
        onChange={handleSelectChange}
      />
    </div>
  );
}
