import React from 'react'
import s from './ProductDescription.module.css'
import { useDispatch } from 'react-redux';
import Button from '../ReusableElements/Button/Button';
import { root_url } from '../../global'
import { addProductToTotalCartAction } from '../../store/cart_reducer/cart_reducer';

export default function ProductDescription({product}) {
  const dispatch = useDispatch()

  return (
    <div className='px-5 md:px-14 pb-14'>
        {product.map(el => 
            <>
                <h2 className='h2'>{el.title}</h2>
                <div className={s.info_block}>
                    <div style={{backgroundImage: `url(${root_url}${el.image})`, backgroundSize: 'cover'}} className={s.image} />
                    <div className='md:pl-10 md:w-2/3'>
                        <div className='flex justify-between items-center'>
                            <div className={'h2'}>${el.price}</div>
                            <div>{el.discont_price && el.discont_price}</div>
                            <div>-{Math.floor(el.price - (el.price * el.discont_price / 100))}%</div>
                        </div>
                        {/* <Button onClick={() => dispatch(addProductToTotalCartAction({...el}))} name='Add to cart' className='primary' /> */}
                        <div>Description
                            <p>{el.description}</p>    
                        </div>
                    </div>
                </div>
            </>
        )}
    </div>
  )
}
