import React from 'react'
import SortingBlock from '../components/SortingBlock/SortingBlock';
import ProductCard from '../components/ProductCard/ProductCard';
import Container from '../components/UI/Container/Container';
import Search from '../assets/icons/Search';


export default function Products({products}) {

  return (
    <Container>
      <h1 className='h2'>All products</h1>
      <SortingBlock />
      {products.lenght > 1 ? 
        <div className='cards_container'>
          {products?.filter(el => el.show_product).map(el => <ProductCard key={el.id} {...el} />)}        
        </div> :
        <>
          <p className='h4'>Unfortunately, no products were found for your request</p>
          <Search /> 
          <p>Please clean the filter and try again</p>  
        </>

      }
    </Container>
  )
}
