    { value: 'option1', label: 'Option 1' },
    { value: 'option2', label: 'Option 2' },
    { value: 'option3', label: 'Option 3' },
    // Добавьте нужные вам опции
  ];

  // Обработчик события при выборе значения
  const handleSelectChange = (selectedOption) => {
