import React from 'react'
import s from './ProductCard.module.css'
import { Link } from 'react-router-dom'
import { root_url } from '../../global'
import Cart from '../../assets/icons/Cart'
import { addProductToTotalCartAction } from '../../store/cart_reducer/cart_reducer';
import { useDispatch } from 'react-redux';

export default function ProductCard({title, price, description, image, id}) {
  const dispatch = useDispatch()
  return (
    <div className={s.card_container}>
      <Link className={s.card} to={`/products/${id}`}>
        <div style={{backgroundImage: `url(${root_url}${image})`, backgroundSize: 'cover'}} className={s.products_card_img} />
        <h6 className={s.card_title}>{title}</h6>
        <div className={s.card_price}>${price}</div>
      </Link>     
      <div>
        <Cart onClick={() => dispatch(addProductToTotalCartAction({title, price, description, image, id}))} />
      </div>
    </div>

  )
}
