import React, { useState, useEffect } from 'react';
import Button from '../Button/Button';
import { useLocation } from 'react-router-dom';

export default function ScrollToTheTop() {
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };

  const location = useLocation();
  const needed_pages = !location.pathname === '/wishlist' & !location.pathname === '/cart' & !location.pathname === '/thankyou' & !location.pathname === '/product'
console.log(needed_pages)
  return (
    <div className='w-1/2 lg:w-1/6 mx-auto mt-10 pt-10'>
        <Button 
            name='Stroll to the top' 
            className='primary' 
            onClick={scrollToTop}
        />
    </div>
  )
}
