import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getProductsByCategory } from '../store/reguests'
import { useParams } from 'react-router-dom'
import ProductCard from '../components/ProductCard/ProductCard'
import SortingBlock from '../components/SortingBlock/SortingBlock';


export default function ProductByCategory() {
  const { id } = useParams()
  const dispatch = useDispatch()

  useEffect(() => dispatch(getProductsByCategory(id)), [])
  const data = useSelector(state => state.products_by_category)
  const title = data.category
  const products = data.data
  const updated_products = products.map(el => ({...el, show_product: true}))

  return (
    <div>
      <h1>{title?.title }</h1>
      <SortingBlock />
      <div className='cards_container'>
        {updated_products?.map(el => <ProductCard key={el.id} {...el} />)}
      </div>      
    </div>

  )
}
