import React from 'react'
import s from './ProductDescription.module.css'
import { useDispatch } from 'react-redux';
import { addProductToCartAction } from '../../store/precard_reducer/precard_reducer';
import Button from '../ReusableElements/Button/Button';

export default function ProductDescription({product}) {
  const dispatch = useDispatch()

  return (
    <div>
        {product.map(el => 
            <>
                <h2>{el.title}</h2>
                <div>
                    <div style={{backgroundImage: `url(http://localhost:3333/${el.image})`, backgroundSize: 'cover'}} className={s.image} />
                    <div>
                        <div>${el.price}</div>
                        {el.discont_price && <div>{el.discont_price}</div>}
                        <div>посчитать на сколько в итоге скидка</div>
                        <Button onClick={() => dispatch(addProductToCartAction(el.id))} name='Add to cart' />
                    </div>
                    <p>{el.description}</p>    
                </div>

            </>
        )}
    </div>
  )
}
