import React, { useState } from 'react'
import s from './CartPreviewWindow.module.css'
import CloseButton from '../CloseButton/CloseButton'

export default function CartPreviewWindow({ id, title, image, discont_price, description, price, closeCartPreview }) {
  const [showWindow, setShowWindow] = useState(false);

  const openCartPreview = () => {
    setShowWindow(true);
  };

  const closeCartPreviewLocal = () => {
    setShowWindow(false);
    closeCartPreview();
  };

  return (
    <div className={`fixed top-0 right-0 transform transition-transform duration-300 ${showWindow ? 'translate-x-0' : 'translate-x-full'}`}>
      <button onClick={openCartPreview}>Open Cart Preview</button>
      {showWindow && (
        <div className="bg-green w-80 h-[500px] rounded-lg shadow-lg p-4">
          <span onClick={closeCartPreviewLocal}>
            <CloseButton />
          </span>
          <div>
            <h2>{title}</h2>
            <img src={image} alt={title} />
            <p>{description}</p>
            <p>Price: {price}</p>
            <p>Discounted Price: {discont_price}</p>
          </div>
        </div>
      )}
    </div>
  )
}
