import React, { useState, useEffect } from 'react';
import Button from '../Button/Button';

export default function ScrollToTheTop() {
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };

  return (
    <div className='w-1/2 lg:w-1/6 mx-auto mt-10 pt-10'>
        <Button 
            name='Stroll to the top' 
            className='primary' 
            onClick={scrollToTop}
        />
    </div>
  )
}
