import React from 'react'
import s from './ProductCard.module.css'
import { Link } from 'react-router-dom'
import { root_url } from '../../global'
import Cart from '../../assets/icons/Cart'
import { addProductToTotalCartAction } from '../../store/cart_reducer/cart_reducer';
import { useDispatch } from 'react-redux';
import Heart from '../../assets/icons/Heart';
import Button from '../ReusableElements/Button/Button';
import { addProductToWishlistAction } from '../../store/wishlist_reducer/wishlist_reducer'

export default function ProductCard(product) {
  const dispatch = useDispatch()
  const currentPath = window.location.pathname;
  const isWishlist = currentPath === '/wishlist'
  console.log(currentPath)

  return (
    <div className={s.card_container}>
      <Link className={s.card} to={`/products/${product.id}`}>
        <div style={{backgroundImage: `url(${root_url}${product.image})`, backgroundSize: 'cover'}} className={s.products_card_img} />
        <h6 className={s.card_title}>{product.title}</h6>
        <div className={s.card_price}>${product.price}</div>
      </Link>   
      <div className={s.hover_buttons_container}>
        <Button className='primary' onClick={() => dispatch(addProductToTotalCartAction({...product}))} name={<Cart />} />
        {!isWishlist && <Button className='primary' onClick={() => dispatch(addProductToWishlistAction({...product}))} name={<Heart />} /> }
      </div>  
    </div>
  )
}
