import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getCategoryName, getProductsByCategory } from '../store/reguests'
import { useParams } from 'react-router-dom'
import ProductCard from '../components/ProductCard/ProductCard'
import s from './pages.module.css'
import SortingBlock from '../SortingBlock/SortingBlock'

export default function ProductByCategory() {
  const { id } = useParams()
  const dispatch = useDispatch()
  useEffect(() => dispatch(getProductsByCategory(id)), [])

  const products = useSelector(state => state.products_by_category)

  useEffect(() => dispatch(getCategoryName(id)), [])

  const category = useSelector(state => state.category_name)
  getCategoryName(id)
  debugger

  return (
    <div>
      {/* <h1>{category.title}</h1> */}
      <SortingBlock />
      <div className={s.card_container}>
        {products.map(el => <ProductCard key={el.id} {...el} />)}
      </div>      
    </div>

  )
}
