import React, { useContext, useState } from 'react';
import SearchIcon from '../../../assets/icons/Search';
import { Context } from '../../../context';
import s from './Search.module.css';

export default function Search() {
  const [query, setQuery] = useState('');
  const { products_state } = useContext(Context);
  const [showDropdown, setShowDropdown] = useState(false);


  const handleSearch = () => {
    const filteredResults = products_state.filter(item => {
      if (item.name && query && query.trim() !== '') {
        return item.name.toLowerCase().includes(query.toLowerCase());
      }
      return false;
    });
    return filteredResults;
  };

  return (
    <div className={s.search_wrapper}>
      <div className={s.search_input}>
        <SearchIcon />
        <input
            type="text"
            placeholder="Search..."
            value={query}
            onChange={e => {
                setQuery(e.target.value);
                setShowDropdown(true); // Показать дропдаун при изменении инпута
            }}
            className={s.inner_input}
        />
      </div>

      <div className="mt-4">
        {query.length > 0 && (
          <div>
            {handleSearch().map(result => (
              <div key={result.id} className={s.input}>
                {result.name}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
