import React, { useState, useEffect } from 'react'
import s from './MoodSwitcher.module.css'

import DarkMood from '../../../assets/icons/DarkMood';
import LightMood from '../../../assets/icons/LightMood';

export default function MoodSwitcher() {
    const [isDarkMode, setIsDarkMode] = useState(false);

    useEffect(() => {
        // Проверяем, есть ли сохраненное состояние в локальном хранилище
        const savedTheme = localStorage.getItem('theme');
        if (savedTheme === 'dark') {
            setIsDarkMode(true);
        } else {
            setIsDarkMode(false);
        }
    }, []);

    useEffect(() => {
        // Обновляем класс темы при изменении состояния
        if (isDarkMode) {
            document.body.classList.add('dark');
            localStorage.setItem('theme', 'dark'); // Сохраняем выбранную тему
        } else {
            document.body.classList.remove('dark');
            localStorage.setItem('theme', 'light'); // Сохраняем выбранную тему
        }
    }, [isDarkMode]);

    const toggleTheme = () => {
        setIsDarkMode(!isDarkMode); // Изменяем состояние для переключения темы
    };
  return (
    <div className={s.theme}>
        {isDarkMode ? <DarkMood onClick={toggleTheme} /> : <LightMood onClick={toggleTheme} />}
    </div>
  )
}
