import React, { useContext, useState } from 'react';
import SearchIcon from '../../../assets/icons/Search';
import { Context } from '../../../context';
import s from './Search.module.css';
import Dropdown from './Dropdown';

export default function Search() {
  const [query, setQuery] = useState('');
  const { products_state } = useContext(Context);
  const [showDropdown, setShowDropdown] = useState(false);


  const handleSearch = () => {
    const filteredResults = products_state.filter(item => {
      if (item.name && query && query.trim() !== '') {
        return item.name.toLowerCase().includes(query.toLowerCase());
      }
      return false;
    });
    return filteredResults;
  };

  return (
    <div className={s.search_wrapper}>
    <div className={s.search_input}>
      <SearchIcon />
      <input
        type="text"
        placeholder="Search..."
        value={query}
        onChange={e => {
          setQuery(e.target.value);
          setShowDropdown(true); // Показать дропдаун при изменении инпута
        }}
        className={s.inner_input}
      />
    </div>

    {showDropdown && query && query.trim() !== '' && (
      <Dropdown results={handleSearch()} />
    )}
  </div>
  );
}
