import { loadCategoriesAction } from './Categories_reducer/Categories_reducer'
import { loadAllProductsAction, loadProductsAction } from './Products_reducer/Products_reducer'

export const getCategories = callback => {
    fetch('http://localhost:3333/categories/all')
    // fetch('https://fakestoreapi.com/products/categories')
    .then(res => res.json())
    .then(res => console.log(res))
}

export const getProductsByCategory = category => {
    return dispatch => {
        fetch(`https://fakestoreapi.com/products/category/${category}`)
            .then(res=>res.json())
            .then(json=>dispatch(loadProductsAction(json)))        
    }

}

export const getAllProducts = callback => {
    fetch('https://fakestoreapi.com/products')
    .then(res=>res.json())
    .then(json=>callback(loadAllProductsAction(json)))
}
