import React, { useState, useEffect, useRef } from 'react';
import s from './SortingBlock.module.css';
import SortForm from './SortForm/SortForm';
import FilterForm from './FilterForm/FilterForm';
import CheapProduct from './CheapProduct/CheapProduct';
import { useLocation } from 'react-router-dom';
import Filter from '../../assets/icons/Filter';
import CloseButton from '../UI/CloseButton/CloseButton';

export default function SortingBlock() {
  const [isFilter, setIsFilter] = useState(false);
  const location = useLocation();
  const sales_page = location.pathname === '/sales';

  const sortingWrapperRef = useRef(null);

  useEffect(() => {
    function handleClickOutside(event) {
      if (sortingWrapperRef.current && !sortingWrapperRef.current.contains(event.target)) {
        setIsFilter(false);
      }
    }

    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleClick = () => {
    setIsFilter((prevIsFilter) => !prevIsFilter);
  };

  const handleIconClick = (event) => {
    event.stopPropagation();
    handleClick();
  };

  return (
    <div className={s.wrapper}>
      <div onClick={handleIconClick} className={s.icon_wrapper}>
        <Filter />
      </div>
      {isFilter && (
        <div className={s.sorting_wrapper} ref={sortingWrapperRef}>
          <CloseButton handleClick={handleClick} />
          <FilterForm />
          {!sales_page && <CheapProduct />}
          <SortForm />
        </div>
      )}
    </div>
  );
}
