import React, { useContext, useState } from 'react'; // Добавлен useState
import s from './Navbar.module.css';
import { NavLink, Link } from 'react-router-dom';
import Cart from '../../assets/icons/Cart';
import logo from '../../assets/logo.png';
import MoodSwitcher from './MoodSwitcher/MoodSwitcher';
import Button from '../UI/Button/Button';
import Badge from '../UI/Badge/Badge';
import Heart from '../../assets/icons/Heart';
import { Context } from '../../context';
import Sales from '../../assets/icons/Sales';
import HomeIcon from '../../assets/icons/HomeIcon';
import Categories from '../../assets/icons/Categories';
import Products from '../../assets/icons/Products';
import Menuicon from '../../assets/icons/MenuIcon';

export default function Navbar({ cart_number, wish_number, openMenu }) {
    const { isDarkMode, toggleTheme, isMobile } = useContext(Context);
    const [isMenuOpen, setIsMenuOpen] = useState(false); // Добавлено состояние для отслеживания состояния меню

    // Функция для открытия и закрытия меню
    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    return (
        <div>
            {isMobile ? (
                <>
                    {isMenuOpen ? (
                        <div className='relative'>
                            <div className={s.nav}>
                                <div className={s.left_part}>
                                    <Link to={'/'}>
                                        <HomeIcon /> Home page
                                    </Link>
                                    {/* Другие ссылки */}
                                </div>
                            </div>
                        </div>
                    ) : (
                        <div className={s.top_line}>
                            <NavLink to={'/cart'} className='relative'>
                                <Cart isDarkMode={isDarkMode} /> Cart
                                {cart_number > 0 && <Badge number={cart_number} />}
                            </NavLink>
                            <MoodSwitcher isDarkMode={isDarkMode} toggleTheme={toggleTheme} />
                            <div
                                className='w-10 h-10 cursor-pointer flex justify-end items-center'
                                onClick={toggleMenu} // Используем toggleMenu для открытия и закрытия меню
                            >
                                <Menuicon />
                            </div>
                        </div>
                    )}
                </>
            ) : (
                <div className={s.nav}>
                    <div className={s.left_part}>
                        <Link to={'/'}>
                            {isMobile ? <HomeIcon /> : <img src={logo} alt='logo' />}
                        </Link>
                        {/* Другие ссылки */}
                    </div>
                </div>
            )}
        </div>
    );
}
