import React, { useContext } from 'react'
import ProductCard from '../ProductCard/ProductCard'
import Container from '../ReusableElements/Container/Container'
import { Context } from '../../context'

export default function SaleBlock({products}) {
  const products_state = useContext(Context)
  return (
    <Container>
        <div className='cards_container'>
            {products
                ?.filter(el => el.discont_price !== null)
                .map(el => <ProductCard key={el.id} {...el} />)
            }
        </div> 
    </Container>
  )
}
