import React, { useState, useEffect } from 'react';
import s from './Navbar.module.css';
import { NavLink, Link } from 'react-router-dom';
import Cart from '../../assets/icons/Cart';
import logo from '../../assets/logo.png';
import LightMood from '../../assets/icons/LightMood';
import DarkMood from '../../assets/icons/DarkMood';
import ThemeSwitcher from '../../assets/icons/ThemeSwitcher';

export default function Navbar() {
    const [isDarkMode, setIsDarkMode] = useState(false);

    useEffect(() => {
        // Проверяем, есть ли сохраненное состояние в локальном хранилище
        const savedTheme = localStorage.getItem('theme');
        if (savedTheme === 'dark') {
            setIsDarkMode(true);
        } else {
            setIsDarkMode(false);
        }
    }, []);

    useEffect(() => {
        // Обновляем класс темы при изменении состояния
        if (isDarkMode) {
            document.body.classList.add('dark');
            localStorage.setItem('theme', 'dark'); // Сохраняем выбранную тему
        } else {
            document.body.classList.remove('dark');
            localStorage.setItem('theme', 'light'); // Сохраняем выбранную тему
        }
    }, [isDarkMode]);

    const toggleTheme = () => {
        setIsDarkMode(!isDarkMode); // Изменяем состояние для переключения темы
    };

    return (
        <div className={s.nav}>
            <div className={s.left_part}>
                <Link to={'/'}>
                    <img src={logo} alt='logo' />
                </Link>
                <Link to={'/categories'} className={s.nav_button}>Catalog</Link>
            </div>
            <div className={s.right_part}>
                <NavLink to={'/'}>
                    <h4>Home page</h4>
                </NavLink>
                <NavLink to={'/products'}>
                    <h4>All products</h4>
                </NavLink>
                <NavLink to={'/sales'}>
                    <h4>All sales</h4>
                </NavLink>
                <NavLink to={'/cart'}><Cart /></NavLink>
            </div>
        </div>
    );
}
