const ADD_PRODUCTS_TO_CART = 'ADD_PRODUCTS_TO_CART'
const INCREMENT_CART = 'INCREMENT_CART'
const DECREMENT_CART = 'DECREMENT_CART'

export const addProductToCartAction = payload => ({type: ADD_PRODUCTS_TO_CART, payload})
export const increment_cart = payload => ({type: INCREMENT_CART})
export const decrement_cart = payload => ({type: DECREMENT_CART})

export const precart_reducer = (state = [], action) => {
    console.log(state)
    if (action.type === ADD_PRODUCTS_TO_CART) {
        return [...state, action.payload]
    } else 
    if (action.type === INCREMENT_CART) {
        state.filter(el => el.id === action.payload).count++
        return [...state]
    } else 
    if (action.type === DECREMENT_CART) {
        const element = state.filter(el => el.id === action.payload)
        element > 0 && element.count--
        return [...state]
    } 
    else return state
}
